import "core-js/modules/es.array.push.js";
import { APIService } from '@/core/util/services/api.service';
import { required, validEmailFormat, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { OxdDialog } from '@ohrm/oxd';
const subscriberModel = {
  name: '',
  email: ''
};
export default {
  name: 'EditSubscriber',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    data: {
      type: Object,
      default: () => ({})
    }
  },
  emits: ['close'],
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/admin/email-subscriptions/${props.data.subscriptionId}/subscribers`);
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      subscriber: {
        ...subscriberModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        email: [required, validEmailFormat, shouldNotExceedCharLength(100)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.subscriber.name = data.name;
      this.subscriber.email = data.email;
      // Fetch list data for unique test
      return this.http.getAll();
    }).then(response => {
      const {
        data
      } = response.data;
      if (data) {
        this.rules.email.push(v => {
          const index = data.findIndex(item => item.email == v);
          if (index > -1) {
            const {
              id
            } = data[index];
            return id != this.data.id ? this.$t('general.already_exists') : true;
          } else {
            return true;
          }
        });
      }
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        ...this.subscriber
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.subscriber = {
        ...subscriberModel
      };
      this.$emit('close', true);
    }
  }
};
