import { required, shouldNotExceedCharLength, validPhoneNumberFormat } from '@ohrm/core/util/validation/rules';
const emergencyContactModel = {
  name: '',
  relationship: '',
  homePhone: '',
  officePhone: '',
  mobilePhone: ''
};
export default {
  name: 'SaveEmergencyContact',
  props: {
    http: {
      type: Object,
      required: true
    }
  },
  emits: ['close'],
  data() {
    return {
      isLoading: false,
      contact: {
        ...emergencyContactModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        relationship: [required, shouldNotExceedCharLength(100)],
        homePhone: [validPhoneNumberFormat, shouldNotExceedCharLength(30), v => {
          return v !== '' || this.contact.mobilePhone !== '' || this.contact.officePhone !== '' || this.$t('pim.at_least_one_phone_number_is_required');
        }],
        mobilePhone: [validPhoneNumberFormat, shouldNotExceedCharLength(30)],
        officePhone: [validPhoneNumberFormat, shouldNotExceedCharLength(30)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.contact
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.contact = {
          ...emergencyContactModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
