import { computed } from 'vue';
import { formatDate, parseDate } from '@/core/util/helper/datefns';
import useDateFormat from '@/core/util/composable/useDateFormat';
import useLocale from '@/core/util/composable/useLocale';
import usei18n from '@/core/util/composable/usei18n';
import useEmployeeNameTranslate from '@/core/util/composable/useEmployeeNameTranslate';
const defaultPic = `${window.appGlobal.publicPath}/images/default-photo.png`;
export default {
  name: 'ReviewSummary',
  props: {
    employee: {
      type: Object,
      required: true
    },
    jobTitle: {
      type: String,
      required: true
    },
    status: {
      type: Number,
      required: true
    },
    reviewPeriodStart: {
      type: String,
      required: true
    },
    reviewPeriodEnd: {
      type: String,
      required: true
    },
    dueDate: {
      type: String,
      required: true
    },
    loading: {
      type: Boolean,
      required: true
    },
    finalRating: {
      type: Number,
      default: 0
    }
  },
  setup(props) {
    const {
      $t
    } = usei18n();
    const {
      locale
    } = useLocale();
    const {
      jsDateFormat
    } = useDateFormat();
    const {
      $tEmpName
    } = useEmployeeNameTranslate();
    const statusOpts = [{
      id: 1,
      label: $t('performance.inactive')
    }, {
      id: 2,
      label: $t('performance.activated')
    }, {
      id: 3,
      label: $t('performance.in_progress')
    }, {
      id: 4,
      label: $t('performance.completed')
    }];
    const reviewDateFormat = date => formatDate(parseDate(date), jsDateFormat, {
      locale
    });
    const imgSrc = computed(() => props.employee.empNumber ? `${window.appGlobal.baseUrl}/pim/viewPhoto/empNumber/${props.employee.empNumber}` : defaultPic);
    const reviewStatus = statusOpts.find(el => el.id === props.status).label;
    const reviewPeriod = `${reviewDateFormat(props.reviewPeriodStart)} - ${reviewDateFormat(props.reviewPeriodEnd)}`;
    const reviewDueDate = reviewDateFormat(props.dueDate);
    const employeeName = computed(() => {
      return $tEmpName(props.employee, {
        includeMiddle: true,
        excludePastEmpTag: false
      });
    });
    return {
      imgSrc,
      reviewStatus,
      reviewPeriod,
      reviewDueDate,
      employeeName
    };
  }
};
