import { APIService } from '@ohrm/core/util/services/api.service';
import { required } from '@/core/util/validation/rules';
import { OxdDialog } from '@ohrm/oxd';
import { maxFileSize, shouldNotExceedCharLength, validFileTypes } from '@ohrm/core/util/validation/rules';
const attachmentModel = {
  attachment: null,
  description: ''
};
export default {
  name: 'SaveAttachment',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    requestId: {
      type: Number,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  emits: ['close'],
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `api/v2/claim/requests/${props.requestId}/attachments`);
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      attachment: {
        ...attachmentModel
      },
      rules: {
        description: [shouldNotExceedCharLength(200)],
        attachment: [required, maxFileSize(this.maxFileSize), validFileTypes(this.allowedFileTypes)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.attachment
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.attachment = {
          ...attachmentModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
