import "core-js/modules/es.array.push.js";
import { digitsOnlyWithDecimalPoint, maxCurrency, required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { OxdSwitchInput } from '@ohrm/oxd';
const salComponentModel = {
  name: '',
  salaryAmount: '',
  comment: '',
  payGradeId: null,
  payFrequencyId: null,
  currencyId: null
};
const directDepositModel = {
  directDepositAccount: '',
  directDepositAccountType: null,
  directDepositRoutingNumber: '',
  directDepositAmount: ''
};
export default {
  name: 'SaveSalaryComponent',
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  props: {
    http: {
      type: Object,
      required: true
    },
    paygrades: {
      type: Array,
      default: () => []
    },
    payFrequencies: {
      type: Array,
      default: () => []
    },
    currencies: {
      type: Array,
      default: () => []
    },
    accountTypes: {
      type: Array,
      default: () => []
    }
  },
  emits: ['close'],
  data() {
    return {
      isLoading: false,
      includeDirectDeposit: false,
      salaryComponent: {
        ...salComponentModel
      },
      directDeposit: {
        ...directDepositModel
      },
      accountType: '',
      usableCurrencies: [],
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        salaryAmount: [required, digitsOnlyWithDecimalPoint, maxCurrency(1000000000)],
        comment: [shouldNotExceedCharLength(250)],
        currencyId: [required],
        directDepositAccount: [required, shouldNotExceedCharLength(100)],
        directDepositAccountType: [required],
        accountType: [required, shouldNotExceedCharLength(20)],
        directDepositRoutingNumber: [required, shouldNotExceedCharLength(9), digitsOnlyWithDecimalPoint],
        directDepositAmount: [required, digitsOnlyWithDecimalPoint, maxCurrency(1000000000)]
      }
    };
  },
  computed: {
    showOptionalAccountType() {
      return this.directDeposit.directDepositAccountType?.id == 'OTHER';
    },
    minAmount() {
      return this.currencyInfo?.minAmount;
    },
    maxAmount() {
      return this.currencyInfo?.maxAmount;
    },
    currenciesOpts() {
      const paygrade = this.salaryComponent.payGradeId?.id;
      if (!paygrade) {
        return this.currencies;
      } else if (paygrade && this.usableCurrencies.length > 0) {
        return this.currencies.filter(item => this.usableCurrencies.findIndex(currency => currency.id === item.id) > -1);
      } else {
        return [];
      }
    },
    currencyInfo() {
      return this.usableCurrencies.find(item => item.id === this.salaryComponent.currencyId?.id);
    }
  },
  watch: {
    'salaryComponent.payGradeId': function (newVal) {
      if (newVal?.id) {
        this.isLoading = true;
        this.http.request({
          url: `/api/v2/admin/pay-grades/${newVal.id}/currencies`,
          method: 'GET',
          params: {
            limit: 0
          }
        }).then(response => {
          const {
            data
          } = response.data;
          this.usableCurrencies = data.map(item => {
            return {
              id: item.currencyType.id,
              name: item.currencyType.name,
              minAmount: item.minSalary,
              maxAmount: item.maxSalary
            };
          });
          const currency = this.salaryComponent.currencyId;
          const currencyIndex = this.usableCurrencies.findIndex(item => item.id === currency?.id);
          this.salaryComponent.currencyId = currencyIndex === -1 ? null : this.salaryComponent.currencyId;
        }).then(() => {
          this.isLoading = false;
        });
      } else {
        this.usableCurrencies = [];
      }
    }
  },
  mounted() {
    this.$nextTick(() => {
      this.rules.salaryAmount.push(v => {
        const min = this.minAmount ? this.minAmount : 0;
        return v >= min || this.$t('pim.should_be_within_min_max_values');
      });
      this.rules.salaryAmount.push(v => {
        const max = this.maxAmount ? this.maxAmount : 999999999;
        return v <= max || this.$t('pim.should_be_within_min_max_values');
      });
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      const accountType = this.showOptionalAccountType ? this.accountType : this.directDeposit.directDepositAccountType?.id;
      this.http.create({
        // Paygrade fields
        salaryComponent: this.salaryComponent.name,
        salaryAmount: this.salaryComponent.salaryAmount,
        payGradeId: this.salaryComponent.payGradeId?.id,
        currencyId: this.salaryComponent.currencyId?.id,
        payFrequencyId: this.salaryComponent.payFrequencyId?.id,
        comment: this.salaryComponent.comment ? this.salaryComponent.comment : null,
        addDirectDeposit: this.includeDirectDeposit,
        // Directdeposi fields
        directDepositAccount: this.includeDirectDeposit ? this.directDeposit.directDepositAccount : undefined,
        directDepositAccountType: this.includeDirectDeposit ? accountType : undefined,
        directDepositAmount: this.includeDirectDeposit ? this.directDeposit.directDepositAmount : undefined,
        directDepositRoutingNumber: this.includeDirectDeposit ? this.directDeposit.directDepositRoutingNumber : undefined
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
