import { OxdDialog } from '@ohrm/oxd';
import useDateFormat from '@/core/util/composable/useDateFormat';
import { formatDate, parseDate } from '@/core/util/helper/datefns';
import useLocale from '@/core/util/composable/useLocale';
export default {
  name: 'LeaveBalanceModal',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    data: {
      type: Object,
      default: () => null
    },
    meta: {
      type: Object,
      default: () => null
    }
  },
  emits: ['close'],
  setup() {
    const {
      jsDateFormat
    } = useDateFormat();
    const {
      locale
    } = useLocale();
    return {
      locale,
      jsDateFormat
    };
  },
  data() {
    return {
      headers: [{
        title: this.$t('leave.leave_status'),
        name: 'status',
        slot: 'left',
        style: {
          flex: 1
        }
      }, {
        title: this.$t('leave.days'),
        name: 'days',
        slot: 'right',
        style: {
          flex: 1,
          textAlign: 'right',
          justifyContent: 'flex-end'
        }
      }]
    };
  },
  computed: {
    items() {
      if (this.data) {
        const {
          taken,
          scheduled,
          pending
        } = this.data;
        return [{
          status: this.$t('leave.taken'),
          days: taken.toFixed(2)
        }, {
          status: this.$t('leave.scheduled'),
          days: scheduled.toFixed(2)
        }, {
          status: this.$t('leave.pending_approval'),
          days: pending.toFixed(2)
        }];
      }
      return [];
    },
    asAtDate() {
      return formatDate(parseDate(this.data?.asAtDate), this.jsDateFormat, {
        locale: this.locale
      });
    },
    leaveType() {
      return this.meta?.leaveType?.name;
    },
    employeeName() {
      const employee = this.meta?.employee;
      if (employee) {
        return `${employee.firstName} ${employee.lastName}
          ${employee.terminationId ? this.$t('general.past_employee') : ''}`;
      }
      return '';
    },
    totalEntitlement() {
      return this.data?.entitled ? `${parseFloat(this.data.entitled).toFixed(2)} Day(s)` : '0.00 Day(s)';
    },
    leaveBalance() {
      return this.data?.balance ? `${parseFloat(this.data.balance).toFixed(2)} Day(s)` : '0.00 Day(s)';
    }
  },
  methods: {
    onCancel() {
      this.$emit('close', true);
    }
  }
};
