import "core-js/modules/es.array.push.js";
import { APIService } from '@/core/util/services/api.service';
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import SaveUserModal from '@/orangehrmTimePlugin/components/SaveUserModal.vue';
import EditActivityModal from '@/orangehrmTimePlugin/components/EditActivityModal.vue';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog.vue';
export default {
  name: 'Activities',
  components: {
    'save-user-modal': SaveUserModal,
    'edit-activity-modal': EditActivityModal,
    'delete-confirmation': DeleteConfirmationDialog
  },
  props: {
    projectId: {
      type: Number,
      required: true
    },
    unselectableIds: {
      type: Array,
      default: () => []
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/time/project/${props.projectId}/users`);
    const serializedFilters = computed(() => {
      return {
        projectId: props.projectId
      };
    });
    const activitiesNormalizer = data => {
      return data.map(item => {
        const selectable = props.unselectableIds.findIndex(id => id == item.id);
        return {
          ...item,
          isSelectable: selectable === -1
        };
      });
    };
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: activitiesNormalizer
    });
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('User Name'),
        style: {
          'flex-basis': '80%'
        }
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          'flex-shrink': 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          }
        }
      }],
      checkedItems: [],
      showSaveUserModal: false,
      showEditActivityModal: false,
      editActivityModalState: null
    };
  },
  methods: {
    onClickAdd() {
      this.showSaveUserModal = true;
    },
    onClickEdit(item) {
      this.editActivityModalState = item.id;
      this.showEditActivityModal = true;
    },
    onCloseModal() {
      this.showSaveUserModal = false;
      this.showEditActivityModal = false;
      this.resetDataTable();
    },
    onClickDeleteSelected() {
      const ids = [];
      this.checkedItems.forEach(index => {
        ids.push(this.items?.data[index].id);
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      const isSelectable = this.unselectableIds.findIndex(id => id == item.id);
      if (isSelectable > -1) {
        return this.$toast.error({
          title: this.$t('general.error'),
          message: this.$t('time.not_allowed_to_delete_project_activities_which_have_time_logged_against')
        });
      }
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          this.$toast.deleteSuccess();
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    }
  }
};
