import { computed, ref } from 'vue';
import { required, validSelection, validDateFormat, endDateShouldBeAfterStartDate, startDateShouldBeBeforeEndDate } from '@/core/util/validation/rules';
import { navigate } from '@/core/util/helper/navigation';
import ReportsTable from '@/core/components/table/ReportsTable';
import ActivityDropdown from '@/orangehrmTimePlugin/components/ActivityDropdown.vue';
import ProjectAutocomplete from '@/orangehrmTimePlugin/components/ProjectAutocomplete.vue';
import usei18n from '@/core/util/composable/usei18n';
import useDateFormat from '@/core/util/composable/useDateFormat';
import { OxdSwitchInput } from '@ohrm/oxd';
const defaultFilters = {
  project: null,
  activity: null,
  fromDate: null,
  toDate: null,
  includeTimesheet: false
};
export default {
  components: {
    'reports-table': ReportsTable,
    'oxd-switch-input': OxdSwitchInput,
    'activity-dropdown': ActivityDropdown,
    'project-autocomplete': ProjectAutocomplete
  },
  props: {
    project: {
      type: Object,
      required: true
    },
    activity: {
      type: Object,
      required: true
    },
    fromDate: {
      type: String,
      required: false,
      default: null
    },
    toDate: {
      type: String,
      required: false,
      default: null
    },
    includeTimesheet: {
      type: Boolean,
      default: false
    }
  },
  setup(props) {
    const {
      $t
    } = usei18n();
    const filters = ref({
      ...defaultFilters,
      fromDate: props.fromDate,
      toDate: props.toDate,
      includeTimesheet: props.includeTimesheet,
      ...(props.project && {
        project: props.project
      }),
      ...(props.activity && {
        activity: props.activity
      })
    });
    const {
      userDateFormat
    } = useDateFormat();
    const rules = {
      project: [required],
      activity: [required, validSelection],
      fromDate: [validDateFormat(userDateFormat), startDateShouldBeBeforeEndDate(() => filters.value.toDate, $t('attendance.from_date_should_be_before_to_date'), {
        allowSameDate: true
      })],
      toDate: [validDateFormat(userDateFormat), endDateShouldBeAfterStartDate(() => filters.value.fromDate, $t('attendance.to_date_should_be_after_from_date'), {
        allowSameDate: true
      })]
    };
    const serializedFilters = computed(() => {
      return {
        projectId: filters.value.project?.id,
        activityId: filters.value.activity?.id,
        fromDate: filters.value.fromDate,
        toDate: filters.value.toDate,
        includeTimesheet: filters.value.includeTimesheet ? 'onlyApproved' : 'all'
      };
    });
    const onClickBack = () => {
      navigate('/time/displayProjectReportCriteria', undefined, {
        projectId: props.project.id,
        fromDate: props.fromDate,
        toDate: props.fromDate,
        includeTimesheet: props.includeTimesheet ? 'onlyApproved' : 'all'
      });
    };
    return {
      rules,
      filters,
      onClickBack,
      serializedFilters
    };
  }
};
