import { required, validDateFormat, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import useDateFormat from '@/core/util/composable/useDateFormat';
const dependentModel = {
  name: '',
  relationshipType: null,
  relationship: '',
  dateOfBirth: ''
};
export default {
  name: 'EditDependent',
  props: {
    http: {
      type: Object,
      required: true
    },
    data: {
      type: Object,
      required: true
    }
  },
  emits: ['close'],
  setup() {
    const {
      userDateFormat
    } = useDateFormat();
    return {
      userDateFormat
    };
  },
  data() {
    return {
      isLoading: false,
      dependent: {
        ...dependentModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        relationshipType: [required],
        relationship: [required, shouldNotExceedCharLength(100)],
        dateOfBirth: [validDateFormat(this.userDateFormat)]
      },
      relationshipOptions: [{
        id: 'child',
        label: this.$t('pim.child')
      }, {
        id: 'other',
        label: this.$t('pim.other')
      }]
    };
  },
  computed: {
    showRelationship() {
      return this.dependent.relationshipType?.id == 'other';
    }
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.dependent = {
        ...dependentModel,
        ...data
      };
      this.dependent.relationshipType = this.relationshipOptions.find(item => item.id === data.relationshipType);
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        ...this.dependent,
        relationshipType: this.dependent.relationshipType?.id
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.dependent = {
          ...dependentModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
