import { APIService } from '@/core/util/services/api.service';
import { required, maxCurrency, digitsOnlyWithTwoDecimalPoints, maxValueShouldBeGreaterThanMinValue } from '@ohrm/core/util/validation/rules';
const payCurrencyModel = {
  currencyId: null,
  minSalary: '',
  maxSalary: ''
};
export default {
  name: 'EditPayCurrency',
  props: {
    payGradeId: {
      type: Number,
      required: true
    },
    data: {
      type: Object,
      required: true
    }
  },
  emits: ['close'],
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/admin/pay-grades/${props.payGradeId}/currencies`);
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      payCurrency: {
        ...payCurrencyModel
      },
      rules: {
        currencyId: [required],
        minSalary: [maxCurrency(1000000000), digitsOnlyWithTwoDecimalPoints],
        maxSalary: [maxCurrency(1000000000), digitsOnlyWithTwoDecimalPoints, maxValueShouldBeGreaterThanMinValue(() => this.payCurrency.minSalary, this.$t('admin.should_be_higher_than_minimum_salary'))]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.payCurrency.name = data.currencyType.name;
      this.payCurrency.minSalary = data.minSalary ? data.minSalary : '0';
      this.payCurrency.maxSalary = data.maxSalary ? data.maxSalary : '0';
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        minSalary: this.payCurrency.minSalary,
        maxSalary: this.payCurrency.maxSalary
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
