import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/nationalities');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      nationality: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.getAll({
      limit: 0
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(v => {
        const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
        return index === -1 || this.$t('general.already_exists');
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.nationality.name
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/nationality');
    }
  }
};
