import { APIService } from '@ohrm/core/util/services/api.service';
import { OxdDialog } from '@ohrm/oxd';
export default {
  name: 'EntitlementUpdateModal',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '');
    return {
      http
    };
  },
  data() {
    return {
      show: false,
      reject: null,
      resolve: null,
      current: '0.00',
      updateAs: '0.00'
    };
  },
  methods: {
    showDialog() {
      return this.http.request({
        method: 'GET',
        url: `/api/v2/leave/employees/${this.data.employee?.id}/leave-entitlements`,
        params: {
          leaveTypeId: this.data.leaveType?.id,
          fromDate: this.data.leavePeriod?.startDate,
          toDate: this.data.leavePeriod?.endDate,
          entitlement: this.data.entitlement
        }
      }).then(response => {
        const {
          data
        } = response.data;
        this.current = data.entitlement?.current ? parseFloat(data.entitlement.current).toFixed(2) : '0.00';
        this.updateAs = data.entitlement?.updateAs ? parseFloat(data.entitlement.updateAs).toFixed(2) : '0.00';
        return new Promise((resolve, reject) => {
          this.resolve = resolve;
          this.reject = reject;
          this.show = true;
        });
      });
    },
    onConfirm() {
      this.show = false;
      this.resolve && this.resolve('ok');
    },
    onCancel() {
      this.show = false;
      this.resolve && this.resolve('cancel');
    }
  }
};
