import "core-js/modules/es.array.push.js";
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { OxdSwitchInput } from '@ohrm/oxd';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
const initialClaimEvent = {
  name: '',
  description: '',
  status: true
};
export default {
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/claim/events');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      claimEvent: {
        ...initialClaimEvent
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        description: [shouldNotExceedCharLength(1000)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.getAll({
      limit: 0
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(value => {
        const index = data.findIndex(item => item.name.toLowerCase() == value.trim().toLowerCase());
        return index === -1 || this.$t('general.already_exists');
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/claim/viewEvents');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.claimEvent,
        name: this.claimEvent.name.trim()
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
