import "core-js/modules/es.array.push.js";
import { ref, computed } from 'vue';
import usei18n from '@/core/util/composable/usei18n';
import { navigate } from '@/core/util/helper/navigation';
import useSort from '@ohrm/core/util/composable/useSort';
import { APIService } from '@/core/util/services/api.service';
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import EventAutocomplete from '@/orangehrmClaimPlugin/components/EventAutocomplete.vue';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog.vue';
const defaultFilters = {
  name: '',
  status: null
};
const defaultSortOrder = {
  'claimEvent.name': 'ASC',
  'claimEvent.status': 'DESC'
};
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog,
    'event-autocomplete': EventAutocomplete
  },
  setup() {
    const filters = ref({
      ...defaultFilters
    });
    const {
      $t
    } = usei18n();
    const {
      sortDefinition,
      sortField,
      sortOrder,
      onSort
    } = useSort({
      sortDefinition: defaultSortOrder
    });
    const serializedFilters = computed(() => {
      return {
        eventId: typeof filters.value.name === 'object' ? filters.value.name.id : null,
        name: typeof filters.value.name === 'string' ? filters.value.name : null,
        status: filters.value.status ? filters.value.status?.id === 1 : null,
        sortField: sortField.value,
        sortOrder: sortOrder.value
      };
    });
    const claimEventDataNormalizer = data => {
      return data.map(item => {
        return {
          id: item.id,
          name: item.name,
          description: item.description,
          status: item.status ? $t('general.active') : $t('performance.inactive')
        };
      });
    };
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/claim/events');
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: claimEventDataNormalizer,
      query: serializedFilters
    });
    onSort(execQuery);
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response,
      response,
      filters,
      sortDefinition
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        title: this.$t('claim.event_name'),
        slot: 'title',
        sortField: 'claimEvent.name',
        style: {
          flex: 3
        }
      }, {
        name: 'status',
        title: this.$t('general.status'),
        sortField: 'claimEvent.status',
        style: {
          flex: 2
        }
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          }
        }
      }],
      checkedItems: [],
      ClaimEventStatuses: [{
        id: 1,
        label: this.$t('general.active')
      }, {
        id: 0,
        label: this.$t('performance.inactive')
      }]
    };
  },
  methods: {
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    },
    async filterItems() {
      await this.execQuery();
    },
    onClickReset() {
      this.filters = {
        ...defaultFilters
      };
      this.filterItems();
    },
    onClickAdd() {
      navigate('/claim/saveEvents');
    },
    onClickDeleteSelected() {
      const ids = [];
      this.checkedItems.forEach(index => {
        ids.push(this.items?.data[index].id);
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    onClickEdit(item) {
      navigate('/claim/saveEvents/{id}', {
        id: item.id
      });
    }
  }
};
