import { useInjectTableProps } from '@ohrm/oxd';
export default {
  name: 'RecordCell',
  props: {
    header: {
      type: Object,
      required: true
    },
    date: {
      type: String,
      default: null
    },
    time: {
      type: String,
      default: null
    },
    offset: {
      type: String,
      default: null
    }
  },
  setup() {
    const {
      screenState
    } = useInjectTableProps();
    return {
      screenState
    };
  },
  computed: {
    showHeader() {
      return !(this.screenState.screenType === 'lg' || this.screenState.screenType === 'xl');
    }
  }
};
