import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength, validFileTypes, maxFileSize } from '@ohrm/core/util/validation/rules';
const initialJobTitle = {
  title: '',
  description: '',
  specification: null,
  note: ''
};
export default {
  props: {
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/job-titles');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      jobTitle: {
        ...initialJobTitle
      },
      rules: {
        title: [required, shouldNotExceedCharLength(100)],
        description: [shouldNotExceedCharLength(400)],
        specification: [validFileTypes(this.allowedFileTypes), maxFileSize(this.maxFileSize)],
        note: [shouldNotExceedCharLength(400)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.getAll({
      limit: 0
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.title.push(v => {
        const index = data.findIndex(item => String(item.title).toLowerCase() == String(v).toLowerCase());
        return index === -1 || this.$t('general.already_exists');
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/admin/viewJobTitleList');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.jobTitle
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
