import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'ReportAutocomplete',
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/reports/defined');
    return {
      http
    };
  },
  methods: {
    async loadReports(serachParam) {
      return new Promise(resolve => {
        if (serachParam.trim()) {
          this.http.getAll({
            name: serachParam
          }).then(({
            data
          }) => {
            resolve(data.data.map(report => {
              return {
                id: report.id,
                label: report.name
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
