import { APIService } from '@/core/util/services/api.service';
import { navigate } from '@ohrm/core/util/helper/navigation';
import EmployeeAutocomplete from '@/core/components/inputs/EmployeeAutocomplete';
import PasswordInput from '@/core/components/inputs/PasswordInput';
import { required, validSelection, shouldNotExceedCharLength, shouldNotLessThanCharLength } from '@/core/util/validation/rules';
import { promiseDebounce } from '@ohrm/oxd';
const userModel = {
  username: '',
  role: null,
  payrollAdmin: false,
  employee: null,
  status: null,
  password: '',
  passwordConfirm: ''
};
export default {
  components: {
    'employee-autocomplete': EmployeeAutocomplete,
    'password-input': PasswordInput
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/users');
    http.setIgnorePath('/api/v2/admin/validation/user-name');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      user: {
        ...userModel
      },
      rules: {
        username: [required, shouldNotLessThanCharLength(5), shouldNotExceedCharLength(40), promiseDebounce(this.validateUserName, 500)],
        role: [required],
        employee: [required, validSelection],
        status: [required]
      },
      userRoles: [{
        id: 1,
        label: this.$t('general.admin')
      }, {
        id: 2,
        label: this.$t('general.ess')
      }],
      userStatuses: [{
        id: 1,
        label: this.$t('general.enabled')
      }, {
        id: 2,
        label: this.$t('general.disabled')
      }]
    };
  },
  methods: {
    onCancel() {
      navigate('/admin/viewSystemUsers');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        username: this.user.username.trim(),
        password: this.user.password,
        status: this.user.status && this.user.status.id === 1,
        userRoleId: this.user.role?.id,
        empNumber: this.user.employee?.id,
        payrollAdmin: this.user.payrollAdmin
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        // go back
        this.onCancel();
      });
    },
    validateUserName(user) {
      return new Promise(resolve => {
        if (user) {
          this.http.request({
            method: 'GET',
            url: `/api/v2/admin/validation/user-name`,
            params: {
              userName: this.user.username.trim()
            }
          }).then(response => {
            const {
              data
            } = response.data;
            return data.valid === true ? resolve(true) : resolve(this.$t('general.already_exists'));
          });
        } else {
          resolve(true);
        }
      });
    }
  }
};
