import ProfileActionHeader from '@/orangehrmPimPlugin/components/ProfileActionHeader';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog';
import { APIService } from '@/core/util/services/api.service';
import usePaginate from '@/core/util/composable/usePaginate';
import SaveEmployeeReportTo from '@/orangehrmPimPlugin/components/SaveEmployeeReportTo';
import EditEmployeeReportTo from '@/orangehrmPimPlugin/components/EditEmployeeReportTo';
import usei18n from '@/core/util/composable/usei18n';
export default {
  name: 'EmployeeSubordinates',
  components: {
    'edit-employee-report-to': EditEmployeeReportTo,
    'save-employee-report-to': SaveEmployeeReportTo,
    'profile-action-header': ProfileActionHeader,
    'delete-confirmation': DeleteConfirmationDialog
  },
  props: {
    empNumber: {
      type: String,
      required: true
    },
    reportingMethods: {
      type: Array,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/pim/employees/${props.empNumber}/subordinates`);
    const {
      $t
    } = usei18n();
    const subordinateNormalizer = data => {
      return data.map(item => {
        return {
          name: `${item.subordinate?.firstName} ${item.subordinate?.lastName} ${item.subordinate.terminationId ? $t('general.past_employee') : ''}`,
          reportingMethod: item.reportingMethod.name,
          subordinateEmpNumber: item.subordinate.empNumber
        };
      });
    };
    const subordinateEndpoint = `/api/v2/pim/employees/${props.empNumber}/subordinates/`;
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: subordinateNormalizer,
      toastNoRecords: false
    });
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response,
      subordinateEndpoint
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('general.name'),
        style: {
          flex: 1
        }
      }, {
        name: 'reportingMethod',
        title: this.$t('pim.reporting_method'),
        style: {
          flex: 1
        }
      }],
      checkedItems: [],
      showSaveModal: false,
      showEditModal: false,
      editModalState: null
    };
  },
  computed: {
    isDisabled() {
      return this.showSaveModal || this.showEditModal;
    },
    tableHeaders() {
      const headerActions = {
        name: 'actions',
        slot: 'action',
        title: this.$t('general.actions'),
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {}
      };
      if (this.$can.delete(`subordinates`)) {
        headerActions.cellConfig.delete = {
          onClick: this.onClickDelete,
          component: 'oxd-icon-button',
          props: {
            name: 'trash'
          }
        };
      }
      if (this.$can.update(`subordinates`)) {
        headerActions.cellConfig.edit = {
          onClick: this.onClickEdit,
          props: {
            name: 'pencil-fill'
          }
        };
      }
      return Object.keys(headerActions.cellConfig).length > 0 ? this.headers.concat([headerActions]) : this.headers;
    }
  },
  methods: {
    onClickDeleteSelected() {
      const ids = this.checkedItems.map(index => {
        return this.items?.data[index].subordinateEmpNumber;
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.subordinateEmpNumber]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    },
    onClickAdd() {
      this.showEditModal = false;
      this.editModalState = null;
      this.showSaveModal = true;
    },
    onClickEdit(item) {
      this.showSaveModal = false;
      this.editModalState = item;
      this.showEditModal = true;
    },
    onSaveModalClose() {
      this.showSaveModal = false;
      this.resetDataTable();
    },
    onEditModalClose() {
      this.showEditModal = false;
      this.editModalState = null;
      this.resetDataTable();
    }
  }
};
