import { required, shouldNotExceedCharLength, validDateFormat, endDateShouldBeAfterStartDate } from '@ohrm/core/util/validation/rules';
import useDateFormat from '@/core/util/composable/useDateFormat';
const workExpModel = {
  company: '',
  jobTitle: '',
  fromDate: '',
  toDate: '',
  comment: ''
};
export default {
  name: 'EditWorkExperience',
  props: {
    http: {
      type: Object,
      required: true
    },
    data: {
      type: Object,
      required: true
    }
  },
  emits: ['close'],
  setup() {
    const {
      userDateFormat
    } = useDateFormat();
    return {
      userDateFormat
    };
  },
  data() {
    return {
      isLoading: false,
      workExperience: {
        ...workExpModel
      },
      rules: {
        company: [required, shouldNotExceedCharLength(100)],
        jobTitle: [required, shouldNotExceedCharLength(100)],
        fromDate: [validDateFormat(this.userDateFormat)],
        toDate: [validDateFormat(this.userDateFormat), endDateShouldBeAfterStartDate(() => this.workExperience.fromDate, this.$t('general.to_date_should_be_after_from_date'))],
        comment: [shouldNotExceedCharLength(200)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.workExperience = {
        ...data
      };
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        ...this.workExperience
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
