import QualificationDropdown from '@/orangehrmPimPlugin/components/QualificationDropdown';
import { required, shouldNotExceedCharLength, max, digitsOnly } from '@ohrm/core/util/validation/rules';
const skillModel = {
  yearsOfExperience: '',
  comments: '',
  skillId: null
};
export default {
  name: 'SaveSkill',
  components: {
    'qualification-dropdown': QualificationDropdown
  },
  props: {
    http: {
      type: Object,
      required: true
    },
    api: {
      type: String,
      required: true
    }
  },
  emits: ['close'],
  data() {
    return {
      isLoading: false,
      skill: {
        ...skillModel
      },
      rules: {
        skillId: [required],
        yearsOfExperience: [digitsOnly, max(100)],
        comments: [shouldNotExceedCharLength(100)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        skillId: this.skill.skillId?.id,
        yearsOfExperience: parseInt(this.skill.yearsOfExperience),
        comments: this.skill.comments !== '' ? this.skill.comments : ''
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
