import { toRefs } from 'vue';
import { APIService } from '@/core/util/services/api.service';
import Timesheet from '@/orangehrmTimePlugin/components/Timesheet.vue';
import useTimesheet from '@/orangehrmTimePlugin/util/composable/useTimesheet';
import TimesheetPeriod from '@/orangehrmTimePlugin/components/TimesheetPeriod.vue';
import TimesheetActions from '@/orangehrmTimePlugin/components/TimesheetActions.vue';
export default {
  components: {
    timesheet: Timesheet,
    'timesheet-period': TimesheetPeriod,
    'timesheet-actions': TimesheetActions
  },
  props: {
    startDate: {
      type: String,
      required: false,
      default: null
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/time/timesheets');
    const {
      state,
      onClickEdit,
      onClickNext,
      onClickSubmit,
      onClickPrevious,
      timesheetPeriod,
      canEditTimesheet,
      canSubmitTimesheet,
      canCreateTimesheet,
      showCreateTimesheet,
      onClickCreateTimesheet
    } = useTimesheet(http, props.startDate);
    return {
      onClickEdit,
      onClickNext,
      onClickSubmit,
      onClickPrevious,
      ...toRefs(state),
      timesheetPeriod,
      canEditTimesheet,
      canSubmitTimesheet,
      canCreateTimesheet,
      showCreateTimesheet,
      onClickCreateTimesheet
    };
  },
  data() {
    return {
      statuses: [{
        id: 1,
        label: this.$t('time.submitted'),
        name: 'Submitted'
      }, {
        id: 2,
        label: this.$t('leave.rejected'),
        name: 'Rejected'
      }, {
        id: 3,
        label: this.$t('time.not_submitted'),
        name: 'Not Submitted'
      }, {
        id: 4,
        label: this.$t('time.approved'),
        name: 'Approved'
      }]
    };
  },
  computed: {
    myTimesheetStatus() {
      return this.statuses.find(item => item.name === this.timesheetStatus)?.label || null;
    }
  }
};
