import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'ExpenseTypeAutocomplete',
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/claim/expenses/types');
    return {
      http
    };
  },
  methods: {
    async loadTypes(serachParam) {
      return new Promise(resolve => {
        if (serachParam.trim()) {
          const params = {
            name: serachParam.trim()
          };
          this.http.getAll(params).then(({
            data
          }) => {
            resolve(data.data.map(type => {
              return {
                id: type.id,
                label: type.name
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
