import { ref } from 'vue';
import useForm from '@ohrm/core/util/composable/useForm';
import { shouldNotExceedCharLength } from '@/core/util/validation/rules';
export default {
  name: 'SaveTimesheetAction',
  props: {
    isLoading: {
      type: Boolean,
      required: true
    },
    rejectTimesheet: {
      type: Function,
      required: true
    },
    approveTimesheet: {
      type: Function,
      required: true
    },
    canRejectTimesheet: {
      type: Boolean,
      required: true
    },
    canApproveTimesheet: {
      type: Boolean,
      required: true
    }
  },
  setup(props) {
    const {
      formRef,
      invalid,
      validate
    } = useForm();
    const comment = ref('');
    const rules = {
      comment: [shouldNotExceedCharLength(250)]
    };
    const onClickApprove = () => {
      validate().then(() => invalid.value === false && props.approveTimesheet(comment.value));
    };
    const onClickReject = () => {
      validate().then(() => invalid.value === false && props.rejectTimesheet(comment.value));
    };
    return {
      rules,
      comment,
      formRef,
      onClickReject,
      onClickApprove
    };
  }
};
