import { navigate } from '@ohrm/core/util/helper/navigation';
import { required, shouldNotExceedCharLength, minValueShouldBeLowerThanMaxValue, maxValueShouldBeGreaterThanMinValue, numberShouldBeBetweenMinAndMaxValue } from '@ohrm/core/util/validation/rules';
import { APIService } from '@/core/util/services/api.service';
import JobtitleDropdown from '@/orangehrmPimPlugin/components/JobtitleDropdown.vue';
import { OxdSwitchInput } from '@ohrm/oxd';
const initialKpi = {
  title: '',
  jobTitle: null,
  minRating: null,
  maxRating: null,
  isDefault: false
};
export default {
  name: 'KpiSave',
  components: {
    'oxd-switch-input': OxdSwitchInput,
    'jobtitle-dropdown': JobtitleDropdown
  },
  props: {
    defaultMinRating: {
      type: Number,
      required: false,
      default: null
    },
    defaultMaxRating: {
      type: Number,
      required: false,
      default: null
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/performance/kpis');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      kpi: {
        ...initialKpi
      },
      rules: {
        title: [required, shouldNotExceedCharLength(100)],
        jobTitle: [required],
        minRating: [required, numberShouldBeBetweenMinAndMaxValue(0, 100), minValueShouldBeLowerThanMaxValue(() => this.kpi.maxRating, this.$t('performance.minimum_rating_should_be_less_than_maximum_rating'))],
        maxRating: [required, numberShouldBeBetweenMinAndMaxValue(0, 100), maxValueShouldBeGreaterThanMinValue(() => this.kpi.minRating, this.$t('performance.maximum_rating_should_be_greater_than_minimum_rating'))]
      }
    };
  },
  beforeMount() {
    this.kpi.minRating = this.defaultMinRating;
    this.kpi.maxRating = this.defaultMaxRating;
  },
  methods: {
    onCancel() {
      navigate('/performance/searchKpi');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        title: this.kpi.title,
        jobTitleId: this.kpi.jobTitle.id,
        minRating: this.kpi.minRating,
        maxRating: this.kpi.maxRating,
        isDefault: this.kpi.isDefault
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
